    DECLARE v_aleatorio BIT default b'0';
    DECLARE v_estatus_conexion varchar(50) DEFAULT NULL;
    DECLARE v_id_persona INT;
    DECLARE v_password varbinary(256);
    DECLARE v_username varchar(60);
    
