        SET v_username =concat_ws("_", (SELECT SUBSTRING(Nombre, 1, 4) FROM personas WHERE v_id_persona =id), v_id_persona, ELT(fn_numero_aleatorio_rangos(1,7), '*','@','?','-','+','/','}'));
		
