		INSERT INTO usuarios VALUES(v_id_persona,
									 v_password,
									 v_tipo,
									 v_estatus_conexion,
									 fn_genera_fecha_registro( (SELECT fecha_registro FROM personas WHERE id= v_id_persona), CURDATE(), "08:00:00", "20:00:00"),
                                     v_username);
